SELECT DISTINCT bgr.identification_number,pupilid,
  Substr(lastname, 1, Instr(lastname, ' ') - 1) AS HO,
  Substr(lastname, Instr(lastname, ' ') + 1)    AS DEM,
 firstname AS TEN,
  --fullname,
  TO_CHAR(ep.BIRTHDAY,'DD/MM/YYYY') BIRTHDAY
  ,bgr.school_code,es1.schoolname,bgcr.high_school_id,es.schoolname
  FROM sams.edu_pupil ep
  LEFT JOIN sams.br_g10_result bgr
  ON ep.pupilid = CAST(bgr.PUPIL_ID AS CHAR(20)) 
  AND bgr.is_deleted = 0 AND bgr.is_active = 1
  AND bgr.is_active = 1 AND bgr.is_deleted = 0
  LEFT JOIN sams.edu_school es1 
  ON es1.schoolid = bgr.school_id
 
  LEFT JOIN sams.br_g10_confirm_result bgcr
  ON  ep.pupilid = CAST(bgcr.PUPIL_ID AS CHAR(20)) 
  AND bgcr.is_deleted = 0 AND bgcr.is_active  =  1 
  LEFT JOIN sams.edu_school es
  ON es.schoolid = bgcr.high_school_id
